const trackPageView=async()=>{const pageUrl=window.location.href;const clickTime=(new Date).toISOString();const data={pageUrl:pageUrl,clickTime:clickTime};const endpoint="https://uncelibate-malisa-limy.ngrok-free.dev/api/track-page-view";try{const response=await fetch(endpoint,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(data)});if(response.ok){console.log("Page view data sent successfully.")}else{console.error("Failed to send page view data. Server status:",response.status)}}catch(error){console.error("Error sending page view data:",error)}};trackPageView();